import "./formSelect.css";

export default function FormSelect(props) {


    let options;
    if (props.opcoesSelect) {
        options = props.opcoesSelect.map((opt) =>
        (<option value={opt.value} key={opt.value}>
            {opt.text}
        </option>));
    }

    const handleCLick = (e) => {
        console.log("ola")
    }

    return (
        <>           
        <label htmlFor={props.idSelect}>{props.label}
            <div> 
                <select type='text' id={props.idSelect} value={props.opcaoSelecionada}
                     onChange={(e) => props.onChangeSelect(e.target.value)}
                    >

                    {options}
                </select>
                <button onclick={(e) => props.handleClick()}>Pesquisar</button>
            </div>
        </label>
        </>
    )
}
